const BASE = ''; // proxy via react dev server

async function fetchJson(path, body){
  const r = await fetch(path, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(body),
  });
  if (!r.ok) throw new Error(`HTTP ${r.status}`);
  return await r.json();
}

export function getQuestions(suspect, crime, answers = {}) {
  return fetchJson('/questions', { suspect, crime, answers });
}

export function evaluate(suspect, crime, answers = {}) {
  return fetchJson('/evaluate', { suspect, crime, answers });
}
