import React, {useState} from 'react';
import {getQuestions, evaluate} from './api';
import logo from './logo.svg';
import './App.css';

function App() {
  const [suspect,setSuspect]=useState('mary');
  const [crime,setCrime]=useState('assassinat');
  const [questions,setQuestions]=useState([]);
  const [answers,setAnswers]=useState({});
  const [result,setResult]=useState(null);

  async function start(){
    const q = await getQuestions(suspect, crime);
    setQuestions(q.questions);
    setAnswers({});
    setResult(null);
  }

  function toggleAnswer(id, val){
    setAnswers(a => ({...a, [id]: val}));
  }

  async function submit(){
    const res = await evaluate(suspect, crime, answers);
    setResult(res);
  }

  return (
    <div className="App">
      <div style={{padding:20}}>
        <h3>EnquÃªte simple</h3>
        <div>
          Suspect: <input value={suspect} onChange={e=>setSuspect(e.target.value)} />
          Crime: <input value={crime} onChange={e=>setCrime(e.target.value)} />
          <button onClick={start}>Poser les questions</button>
        </div>
        <div>
          {questions.map(q=>(
            <div key={q.id}>
              <b>{q.text}</b><br/>
              <button onClick={()=>toggleAnswer(q.id, true)}>Oui</button>
              <button onClick={()=>toggleAnswer(q.id, false)}>Non</button>
              <span> RÃ©ponse: {String(answers[q.id])}</span>
            </div>
          ))}
        </div>
        <div>
          <button onClick={submit}>Evaluer</button>
        </div>
        {result && (
          <div>
            <h4>Verdict: {result.verdict}</h4>
            <div>Preuves: {JSON.stringify(result.supporting)}</div>
            <div>Manquantes: {JSON.stringify(result.missing)}</div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
